import React, { useState } from 'react';
import { api } from '../lib/api';
import { categories } from '../constants/categories';
import { toast } from 'react-hot-toast';
import './AddProduct.css';

const AddProduct = () => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '0.00',
    stock: '1',
    category: {
      main: '',
      sub: ''
    },
    image: null
  });
  const [imagePreview, setImagePreview] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleMainCategoryChange = (e) => {
    const mainCategory = e.target.value;
    setFormData(prev => ({
      ...prev,
      category: {
        main: mainCategory,
        sub: ''
      }
    }));
  };

  const handleSubCategoryChange = (e) => {
    const subCategory = e.target.value;
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        sub: subCategory
      }
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation des catégories
    if (!formData.category.main || !formData.category.sub) {
      toast.error('Veuillez sélectionner une catégorie et une sous-catégorie');
      return;
    }

    setLoading(true);
    try {
      let imageUrl = '';
      if (formData.image) {
        imageUrl = await api.products.uploadImage(formData.image);
      }

      const productData = {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        imageUrl: imageUrl,
        category: {
          main: formData.category.main,
          sub: formData.category.sub
        }
      };

      await api.products.create(productData);
      toast.success('Produit ajouté avec succès');

      // Reset form
      setFormData({
        name: '',
        description: '',
        price: '0.00',
        stock: '1',
        category: {
          main: '',
          sub: ''
        },
        image: null
      });
      setImagePreview('');
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.message || 'Erreur lors de l\'ajout du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard">
      <div className="product-form">
        <h2>Tableau de bord</h2>
        <h3>Gérez vos produits</h3>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nom</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="Nom du produit"
              required
            />
          </div>

          <div className="form-group">
            <label>Prix (€)</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleInputChange}
              step="0.01"
              min="0"
              required
            />
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="Description du produit"
              required
            />
          </div>

          <div className="form-group">
            <label>Catégorie</label>
            <select
              name="category.main"
              value={formData.category.main}
              onChange={handleMainCategoryChange}
              required
            >
              <option value="">Sélectionner une catégorie</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          {formData.category.main && (
            <div className="form-group">
              <label>Sous-catégorie</label>
              <select
                name="category.sub"
                value={formData.category.sub}
                onChange={handleSubCategoryChange}
                required
              >
                <option value="">Sélectionner une sous-catégorie</option>
                {categories
                  .find(cat => cat.id === formData.category.main)
                  ?.subcategories.map(sub => (
                    <option key={sub.id} value={sub.id}>
                      {sub.name}
                    </option>
                  ))}
              </select>
            </div>
          )}

          <div className="form-group">
            <label>Stock</label>
            <input
              type="number"
              name="stock"
              value={formData.stock}
              onChange={handleInputChange}
              min="1"
              required
            />
          </div>

          <div className="form-group">
            <label>Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
            {!imagePreview && <span className="no-file">Aucun fichier n'a été sélectionné</span>}
            {imagePreview && (
              <img
                src={imagePreview}
                alt="Aperçu"
                className="image-preview"
              />
            )}
          </div>

          <button
            type="submit"
            disabled={loading}
            className={loading ? 'loading' : ''}
          >
            {loading ? 'Ajout en cours...' : 'Ajouter le produit'}
          </button>
        </form>
      </div>

      <div className="products-list">
        <h3>Vos produits</h3>
        <div className="category-filter">
          <select>
            <option value="">Toutes les catégories</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>
        <div className="no-products">
          Vous n'avez pas encore de produits.
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
