import React, { useState } from 'react';
import { api } from '../lib/api';
import { toast } from 'react-hot-toast';
import './AddProduct.css';

const AddProduct = () => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '0.00',
    stock: '1',
    category: {
      main: 'Electronics',
      sub: ''
    },
    image: null
  });
  const [imagePreview, setImagePreview] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleMainCategoryChange = (e) => {
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        main: e.target.value
      }
    }));
  };

  const handleSubCategoryChange = (e) => {
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        sub: e.target.value
      }
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const validateForm = () => {
    if (!formData.category.main.trim()) {
      toast.error('Veuillez sélectionner une catégorie principale');
      return false;
    }
    if (!formData.category.sub.trim()) {
      toast.error('Veuillez entrer une sous-catégorie');
      return false;
    }
    if (parseFloat(formData.price) < 0) {
      toast.error('Le prix ne peut pas être négatif');
      return false;
    }
    if (parseInt(formData.stock) < 0) {
      toast.error('Le stock ne peut pas être négatif');
      return false;
    }
    return true;
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '0.00',
      stock: '1',
      category: {
        main: 'Electronics',
        sub: ''
      },
      image: null
    });
    setImagePreview('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      const productToCreate = {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        category: {
          main: formData.category.main,
          sub: formData.category.sub
        }
      };

      if (formData.image) {
        const formDataWithImage = new FormData();
        formDataWithImage.append('image', formData.image);
        const uploadResponse = await api.products.uploadImage(formDataWithImage);
        productToCreate.imageUrl = uploadResponse.imageUrl;
      }

      const response = await api.products.create(productToCreate);
      toast.success('Produit ajouté avec succès');
      resetForm();
    } catch (error) {
      console.error('Erreur:', error);
      toast.error(error.response?.data?.message || 'Erreur lors de l\'ajout du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>Ajouter un nouveau produit</h2>
      <form onSubmit={handleSubmit} className="add-product-form">
        <div className="form-group">
          <label htmlFor="name">Nom du produit</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Prix (dt)</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            step="0.01"
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            type="number"
            id="stock"
            name="stock"
            value={formData.stock}
            onChange={handleInputChange}
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="main-category">Catégorie principale</label>
          <select
            id="main-category"
            value={formData.category.main}
            onChange={handleMainCategoryChange}
            required
          >
            <option value="">Sélectionnez une catégorie</option>
            <option value="Electronics">Électronique</option>
            <option value="Clothing">Vêtements</option>
            <option value="Books">Livres</option>
            <option value="Sports">Sports</option>
            <option value="Home">Maison</option>
            <option value="Other">Autre</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="sub-category">Sous-catégorie</label>
          <input
            type="text"
            id="sub-category"
            value={formData.category.sub}
            onChange={handleSubCategoryChange}
            required
            placeholder="Entrez une sous-catégorie"
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image</label>
          <input
            type="file"
            id="image"
            onChange={handleImageChange}
            accept="image/*"
          />
          {imagePreview && (
            <img
              src={imagePreview}
              alt="Aperçu"
              className="image-preview"
            />
          )}
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Ajout en cours...' : 'Ajouter le produit'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
