import React, { useState } from 'react';
import { api } from '../lib/api';
import { categories } from '../constants/categories';
import { toast } from 'react-hot-toast';
import './AddProduct.css';

const AddProduct = () => {
  const [loading, setLoading] = useState(false);
  const [selectedMainCategory, setSelectedMainCategory] = useState(categories[0].id);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stock: '1',
    category: {
      main: categories[0].id,
      sub: categories[0].subcategories[0].id
    }
  });
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleMainCategoryChange = (e) => {
    const mainCategory = e.target.value;
    setSelectedMainCategory(mainCategory);
    const selectedCategory = categories.find(cat => cat.id === mainCategory);

    setFormData(prev => ({
      ...prev,
      category: {
        main: mainCategory,
        sub: selectedCategory?.subcategories[0].id || ''
      }
    }));
  };

  const handleSubCategoryChange = (e) => {
    const subCategory = e.target.value;
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        sub: subCategory
      }
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      let imageUrl = '';
      if (image) {
        imageUrl = await api.products.uploadImage(image);
      }

      await api.products.create({
        ...formData,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        image: imageUrl
      });

      toast.success('Produit ajouté avec succès');

      // Reset form
      setFormData({
        name: '',
        description: '',
        price: '',
        stock: '1',
        category: {
          main: categories[0].id,
          sub: categories[0].subcategories[0].id
        }
      });
      setImage(null);
      setImagePreview('');
      setSelectedMainCategory(categories[0].id);
    } catch (error) {
      console.error('Error:', error);
      toast.error(error.message || 'Erreur lors de l\'ajout du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product">
      <h2>Ajouter un Nouveau Produit</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nom du Produit</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Prix</label>
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            step="0.01"
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label>Stock</label>
          <input
            type="number"
            name="stock"
            value={formData.stock}
            onChange={handleInputChange}
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label>Catégorie Principale</label>
          <select
            value={formData.category.main}
            onChange={handleMainCategoryChange}
            required
          >
            <option value="">Sélectionner une catégorie</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.icon} {category.name}
              </option>
            ))}
          </select>
        </div>

        {formData.category.main && (
          <div className="form-group">
            <label>Sous-catégorie</label>
            <select
              value={formData.category.sub}
              onChange={handleSubCategoryChange}
              required
            >
              <option value="">Sélectionner une sous-catégorie</option>
              {categories
                .find(cat => cat.id === formData.category.main)
                ?.subcategories.map(sub => (
                  <option key={sub.id} value={sub.id}>
                    {sub.name}
                  </option>
                ))}
            </select>
          </div>
        )}

        <div className="form-group">
          <label>Image du Produit</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
          {imagePreview && (
            <img
              src={imagePreview}
              alt="Preview"
              className="image-preview"
            />
          )}
        </div>

        <button
          type="submit"
          disabled={loading}
          className={loading ? 'loading' : ''}
        >
          {loading ? 'Ajout en cours...' : 'Ajouter le Produit'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
