import React, { useState, useEffect } from 'react';
import { api } from '../lib/api';

const Dashboard = () => {
  const [userStats, setUserStats] = useState({
    orders: [],
    totalSpent: 0,
    recentActivity: []
  });

  const [featuredProducts] = useState([
    {
      id: 1,
      name: 'Wireless Earbuds',
      price: 129.99,
      image: 'https://images.unsplash.com/photo-1572569511254-d8f925fe2cbb?ixlib=rb-1.2.1&auto=format&fit=crop&w=1000&q=80',
      description: 'High-quality wireless earbuds with noise cancellation'
    },
    {
      id: 2,
      name: 'Smart Watch',
      price: 199.99,
      image: 'https://images.unsplash.com/photo-1579586337278-3befd40fd17a?ixlib=rb-1.2.1&auto=format&fit=crop&w=1000&q=80',
      description: 'Feature-rich smartwatch with health tracking'
    },
    {
      id: 3,
      name: 'Laptop Stand',
      price: 49.99,
      image: 'https://images.unsplash.com/photo-1527864550417-7fd91fc51a46?ixlib=rb-1.2.1&auto=format&fit=crop&w=1000&q=80',
      description: 'Ergonomic laptop stand for better posture'
    }
  ]);

  useEffect(() => {
    // Simuler le chargement des données utilisateur
    setUserStats({
      orders: [
        { id: 1, date: '2024-02-27', total: 129.99, status: 'delivered' },
        { id: 2, date: '2024-02-25', total: 199.99, status: 'processing' }
      ],
      totalSpent: 329.98,
      recentActivity: [
        { id: 1, type: 'order', description: 'Order #1234 delivered', date: '2 hours ago' },
        { id: 2, type: 'review', description: 'You reviewed Wireless Earbuds', date: '1 day ago' }
      ]
    });
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Welcome Section */}
        <div className="bg-white rounded-lg shadow-lg overflow-hidden mb-8">
          <div className="bg-orange-600 px-6 py-8">
            <h1 className="text-3xl font-bold text-white">Welcome to Your Dashboard</h1>
            <p className="mt-2 text-orange-100">Track your orders and discover new products</p>
          </div>
          <div className="px-6 py-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-orange-50 rounded-lg p-6">
                <h3 className="text-lg font-semibold text-orange-900">Total Spent</h3>
                <p className="text-3xl font-bold text-orange-600">${userStats.totalSpent}</p>
              </div>
              <div className="bg-orange-50 rounded-lg p-6">
                <h3 className="text-lg font-semibold text-orange-900">Orders</h3>
                <p className="text-3xl font-bold text-orange-600">{userStats.orders.length}</p>
              </div>
              <div className="bg-orange-50 rounded-lg p-6">
                <h3 className="text-lg font-semibold text-orange-900">Reviews</h3>
                <p className="text-3xl font-bold text-orange-600">1</p>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Orders */}
        <div className="bg-white rounded-lg shadow-lg mb-8">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-800">Recent Orders</h2>
          </div>
          <div className="divide-y divide-gray-200">
            {userStats.orders.map(order => (
              <div key={order.id} className="px-6 py-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-900">Order #{order.id}</p>
                    <p className="text-sm text-gray-500">{order.date}</p>
                  </div>
                  <div className="flex items-center">
                    <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                      order.status === 'delivered' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                    </span>
                    <span className="ml-4 text-sm font-medium text-gray-900">${order.total}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Featured Products */}
        <div className="bg-white rounded-lg shadow-lg mb-8">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-800">Featured Products</h2>
          </div>
          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {featuredProducts.map(product => (
                <div key={product.id} className="bg-white rounded-lg border border-gray-200 overflow-hidden">
                  <img src={product.image} alt={product.name} className="w-full h-48 object-cover" />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-900">{product.name}</h3>
                    <p className="mt-1 text-gray-500">{product.description}</p>
                    <div className="mt-4 flex items-center justify-between">
                      <span className="text-lg font-bold text-orange-600">${product.price}</span>
                      <button className="bg-orange-600 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-orange-700">
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="bg-white rounded-lg shadow-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-800">Recent Activity</h2>
          </div>
          <div className="divide-y divide-gray-200">
            {userStats.recentActivity.map(activity => (
              <div key={activity.id} className="px-6 py-4">
                <div className="flex items-center">
                  <div className={`rounded-full p-2 ${
                    activity.type === 'order' ? 'bg-blue-100' : 'bg-green-100'
                  }`}>
                    {activity.type === 'order' ? (
                      <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                      </svg>
                    ) : (
                      <svg className="w-4 h-4 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                      </svg>
                    )}
                  </div>
                  <div className="ml-4">
                    <p className="text-sm font-medium text-gray-900">{activity.description}</p>
                    <p className="text-sm text-gray-500">{activity.date}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
