import { useEffect, useState } from 'react';
import { Search, ShoppingCart, Plus, Minus, X } from 'lucide-react';
import { useCartStore } from '../lib/store';
import { api } from '../lib/api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';

export default function Shop() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [showCart, setShowCart] = useState(false);
  const { items, addItem, removeItem, updateQuantity, total } = useCartStore();
  const navigate = useNavigate();

  const handleAddToCart = (product) => {
    addItem(product);
    toast.success('Produit ajouté au panier');
  };

  const handleCheckout = async () => {
    try {
      if (items.length === 0) {
        toast.error('Votre panier est vide');
        return;
      }

      const orderItems = items.map((item) => ({
        product_id: item.product.id,
        quantity: item.quantity,
        price: item.product.price,
      }));

      await api.orders.create(orderItems);
      useCartStore.getState().clearCart();
      setShowCart(false);
      toast.success('Commande passée avec succès!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error placing order:', error);
      toast.error('Échec de la commande. Veuillez réessayer.');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [selectedCategory, searchQuery]);

  const fetchProducts = async () => {
    try {
      const response = await api.products.getAll(selectedCategory, searchQuery);
      setProducts(response);
    } catch (error) {
      console.error('Error fetching products:', error);
      toast.error('Erreur lors du chargement des produits');
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { id: 'all', name: 'Toutes les catégories' },
    { id: 'electronics', name: 'Électronique' },
    { id: 'clothing', name: 'Vêtements' },
    { id: 'books', name: 'Livres' },
    { id: 'home', name: 'Maison & Jardin' },
    { id: 'sports', name: 'Sports' },
    { id: 'toys', name: 'Jouets & Jeux' },
    { id: 'beauty', name: 'Beauté' },
    { id: 'automotive', name: 'Automobile' },
    { id: 'jewelry', name: 'Bijoux' },
    { id: 'food', name: 'Alimentation' },
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header with Search and Cart */}
      <div className="flex justify-between items-center mb-8">
        <div className="relative max-w-xl w-full">
          <input
            type="text"
            placeholder="Rechercher des produits..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
          />
          <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
        </div>
        <button
          onClick={() => setShowCart(true)}
          className="relative bg-orange-600 text-white p-3 rounded-full hover:bg-orange-700"
        >
          <ShoppingCart className="h-6 w-6" />
          {items.length > 0 && (
            <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold rounded-full h-5 w-5 flex items-center justify-center">
              {items.length}
            </span>
          )}
        </button>
      </div>

      <div className="flex flex-col md:flex-row gap-8">
        {/* Categories Sidebar */}
        <div className="w-full md:w-64 flex-shrink-0">
          <div className="bg-white p-4 rounded-lg shadow-lg sticky top-4">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Catégories</h2>
            <div className="space-y-2">
              {categories.map((category) => (
                <button
                  key={category.id}
                  onClick={() => setSelectedCategory(category.id)}
                  className={`w-full text-left px-4 py-2 rounded-lg text-sm font-medium transition duration-150 ${
                    selectedCategory === category.id
                      ? 'bg-orange-100 text-orange-700'
                      : 'text-gray-600 hover:bg-gray-50'
                  }`}
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Products Grid */}
        <div className="flex-1">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {loading ? (
              <div>Chargement...</div>
            ) : products.length === 0 ? (
              <div>Aucun produit trouvé</div>
            ) : (
              products.map((product) => (
                <div key={product.id} className="bg-white rounded-lg shadow-md p-4">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 object-cover rounded-lg"
                  />
                  <h3 className="mt-4 text-lg font-medium">{product.name}</h3>
                  <p className="text-gray-500">{product.description}</p>
                  <div className="mt-4 flex justify-between items-center">
                    <span className="text-xl font-bold">${product.price}</span>
                    <button
                      onClick={() => handleAddToCart(product)}
                      className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700"
                    >
                      Ajouter au panier
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>

      {/* Shopping Cart Sidebar */}
      {showCart && (
        <div className="fixed inset-0 overflow-hidden z-50">
          <div className="absolute inset-0 overflow-hidden">
            <div className="absolute inset-0 bg-gray-500 bg-opacity-75 transition-opacity" />
            <div className="fixed inset-y-0 right-0 pl-10 max-w-full flex">
              <div className="w-screen max-w-md">
                <div className="h-full flex flex-col bg-white shadow-xl">
                  <div className="flex-1 py-6 overflow-y-auto px-4 sm:px-6">
                    <div className="flex items-start justify-between">
                      <h2 className="text-lg font-medium text-gray-900">Panier</h2>
                      <button
                        className="text-gray-400 hover:text-gray-500"
                        onClick={() => setShowCart(false)}
                      >
                        <X className="h-6 w-6" />
                      </button>
                    </div>

                    <div className="mt-8">
                      {items.length === 0 ? (
                        <p className="text-gray-500">Votre panier est vide</p>
                      ) : (
                        <div className="flow-root">
                          <ul className="divide-y divide-gray-200">
                            {items.map((item) => (
                              <li key={item.product.id} className="py-6 flex">
                                <div className="flex-shrink-0 w-24 h-24 overflow-hidden rounded-md">
                                  <img
                                    src={item.product.image}
                                    alt={item.product.name}
                                    className="w-full h-full object-center object-cover"
                                  />
                                </div>

                                <div className="ml-4 flex-1 flex flex-col">
                                  <div>
                                    <div className="flex justify-between text-base font-medium text-gray-900">
                                      <h3>{item.product.name}</h3>
                                      <p className="ml-4">${item.product.price}</p>
                                    </div>
                                  </div>
                                  <div className="flex-1 flex items-end justify-between text-sm">
                                    <div className="flex items-center">
                                      <button
                                        onClick={() => updateQuantity(item.product.id, Math.max(0, item.quantity - 1))}
                                        className="p-1 rounded-full hover:bg-gray-100"
                                      >
                                        <Minus className="h-4 w-4" />
                                      </button>
                                      <span className="mx-2">{item.quantity}</span>
                                      <button
                                        onClick={() => updateQuantity(item.product.id, item.quantity + 1)}
                                        className="p-1 rounded-full hover:bg-gray-100"
                                      >
                                        <Plus className="h-4 w-4" />
                                      </button>
                                    </div>

                                    <button
                                      type="button"
                                      onClick={() => removeItem(item.product.id)}
                                      className="font-medium text-orange-600 hover:text-orange-500"
                                    >
                                      Supprimer
                                    </button>
                                  </div>
                                </div>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>

                  {items.length > 0 && (
                    <div className="border-t border-gray-200 py-6 px-4 sm:px-6">
                      <div className="flex justify-between text-base font-medium text-gray-900">
                        <p>Total</p>
                        <p>${total.toFixed(2)}</p>
                      </div>
                      <p className="mt-0.5 text-sm text-gray-500">
                        Frais de livraison calculés à la commande.
                      </p>
                      <div className="mt-6">
                        <button
                          onClick={handleCheckout}
                          className="w-full flex justify-center items-center px-6 py-3 border border-transparent rounded-md shadow-sm text-base font-medium text-white bg-orange-600 hover:bg-orange-700"
                        >
                          Commander
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
