import axios from 'axios';

const BASE_URL = 'http://localhost:4990/api';

const getAuthToken = () => localStorage.getItem('token');

const api = {
  orders: {
    create: async (orderItems) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.post(`${BASE_URL}/orders`, orderItems, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  },
  products: {
    getAll: async (category = 'all', search = '') => {
      try {
        const response = await axios.get(`${BASE_URL}/products`, {
          params: {
            category: category,
            search: search
          }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getByUser: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/products/user`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    create: async (productData) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.post(`${BASE_URL}/products`, productData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    delete: async (productId) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.delete(`${BASE_URL}/products/${productId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    uploadImage: async (imageFile) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const formData = new FormData();
        formData.append('image', imageFile);

        const response = await axios.post(`${BASE_URL}/products/upload`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        });
        return response.data.imageUrl;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  },
  admin: {
    checkStatus: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/admin/status`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getAnalytics: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/admin/analytics`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  }
};


export { api };
