import React, { useState } from 'react';
import { api } from '../lib/api';
import { toast } from 'react-hot-toast';
import { categories } from '../constants/categories';
import './AddProduct.css';

const AddProduct = ({ onProductAdded }) => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '0.00',
    stock: '1',
    category: {
      main: '',
      sub: ''
    },
    image: null
  });
  const [imagePreview, setImagePreview] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleMainCategoryChange = (e) => {
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        main: e.target.value,
        sub: ''
      }
    }));
  };

  const handleSubCategoryChange = (e) => {
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        sub: e.target.value
      }
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.description || !formData.price || !formData.stock || !formData.category.main) {
      toast.error('Veuillez remplir tous les champs requis');
      return;
    }

    try {
      setLoading(true);

      // Trouver les noms des catégories sélectionnées
      const selectedMainCategory = categories.find(cat => cat.id === formData.category.main);
      const selectedSubCategory = selectedMainCategory?.subcategories.find(sub => sub.id === formData.category.sub);

      let imageUrl = '';
      if (formData.image) {
        const formDataImg = new FormData();
        formDataImg.append('image', formData.image);
        const response = await api.upload.image(formDataImg);
        imageUrl = response.url;
      }

      const productToCreate = {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        category: {
          main: selectedMainCategory?.name || '',
          sub: selectedSubCategory?.name || ''
        },
        imageUrl
      };

      await api.products.create(productToCreate);
      toast.success('Produit ajouté avec succès');

      // Reset form
      setFormData({
        name: '',
        description: '',
        price: '0.00',
        stock: '1',
        category: {
          main: '',
          sub: ''
        },
        image: null
      });
      setImagePreview('');

      // Notify parent component
      if (onProductAdded) {
        onProductAdded();
      }
    } catch (error) {
      console.error('Erreur:', error);
      toast.error('Erreur lors de l\'ajout du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>Ajouter un nouveau produit</h2>
      <form onSubmit={handleSubmit} className="add-product-form">
        <div className="form-group">
          <label htmlFor="name">Nom du produit</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Prix (dt)</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            step="0.01"
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            type="number"
            id="stock"
            name="stock"
            value={formData.stock}
            onChange={handleInputChange}
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="main-category">Catégorie principale</label>
          <select
            id="main-category"
            value={formData.category.main}
            onChange={handleMainCategoryChange}
            required
          >
            <option value="">Sélectionnez une catégorie</option>
            {categories.map((category) => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="sub-category">Sous-catégorie</label>
          <select
            id="sub-category"
            value={formData.category.sub}
            onChange={handleSubCategoryChange}
            required
            disabled={!formData.category.main}
          >
            <option value="">Sélectionnez une sous-catégorie</option>
            {formData.category.main && 
              categories.find(cat => cat.id === formData.category.main)?.subcategories.map((subCategory) => (
                <option key={subCategory.id} value={subCategory.id}>
                  {subCategory.name}
                </option>
              ))
            }
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="image">Image</label>
          <input
            type="file"
            id="image"
            onChange={handleImageChange}
            accept="image/*"
          />
          {imagePreview && (
            <img
              src={imagePreview}
              alt="Aperçu"
              className="image-preview"
            />
          )}
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Ajout en cours...' : 'Ajouter le produit'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
