import React, { useState } from 'react';
import { api } from '../lib/api';
import { categories } from '../constants/categories';
import { toast } from 'react-hot-toast';
import './AddProduct.css';

const AddProduct = () => {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '0.00',
    stock: '1',
    category: {
      main: 'Electronics',
      sub: ''
    },
    image: null
  });
  const [imagePreview, setImagePreview] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleMainCategoryChange = (e) => {
    const mainCategory = e.target.value;
    setFormData(prev => ({
      ...prev,
      category: {
        main: mainCategory,
        sub: ''
      }
    }));
  };

  const handleSubCategoryChange = (e) => {
    const subCategory = e.target.value;
    setFormData(prev => ({
      ...prev,
      category: {
        ...prev.category,
        sub: subCategory
      }
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, image: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation des catégories
    if (!formData.category.main) {
      toast.error('Veuillez sélectionner une catégorie principale');
      return;
    }
    if (!formData.category.sub) {
      toast.error('Veuillez entrer une sous-catégorie');
      return;
    }

    setLoading(true);
    try {
      let imageUrl = '';
      if (formData.image) {
        imageUrl = await api.products.uploadImage(formData.image);
      }

      const productData = {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        stock: parseInt(formData.stock),
        imageUrl: imageUrl,
        category: {
          main: formData.category.main,
          sub: formData.category.sub
        }
      };

      console.log('Envoi des données:', productData); // Pour le debug
      await api.products.create(productData);
      toast.success('Produit ajouté avec succès');

      // Reset form
      setFormData({
        name: '',
        description: '',
        price: '0.00',
        stock: '1',
        category: {
          main: 'Electronics',
          sub: ''
        },
        image: null
      });
      setImagePreview('');
    } catch (error) {
      console.error('Erreur:', error);
      toast.error(error.response?.data?.message || 'Erreur lors de l\'ajout du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>Ajouter un nouveau produit</h2>
      <form onSubmit={handleSubmit} className="add-product-form">
        <div className="form-group">
          <label>Nom du produit</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Prix (€)</label>
          <input
            type="number"
            name="price"
            value={formData.price}
            onChange={handleInputChange}
            step="0.01"
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label>Stock</label>
          <input
            type="number"
            name="stock"
            value={formData.stock}
            onChange={handleInputChange}
            min="0"
            required
          />
        </div>

        <div className="form-group">
          <label>Catégorie principale</label>
          <select
            value={formData.category.main}
            onChange={handleMainCategoryChange}
            required
          >
            <option value="">Sélectionnez une catégorie</option>
            <option value="Electronics">Électronique</option>
            <option value="Clothing">Vêtements</option>
            <option value="Books">Livres</option>
            <option value="Sports">Sports</option>
            <option value="Home">Maison</option>
            <option value="Other">Autre</option>
          </select>
        </div>

        <div className="form-group">
          <label>Sous-catégorie</label>
          <input
            type="text"
            name="subcategory"
            value={formData.category.sub}
            onChange={handleSubCategoryChange}
            required
            placeholder="Entrez une sous-catégorie"
          />
        </div>

        <div className="form-group">
          <label>Image</label>
          <input
            type="file"
            onChange={handleImageChange}
            accept="image/*"
            required
          />
          {imagePreview && (
            <img
              src={imagePreview}
              alt="Preview"
              className="image-preview"
            />
          )}
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Ajout en cours...' : 'Ajouter le produit'}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
