import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { api } from '../../lib/api';
import { ShoppingBag, Home, LogIn, LogOut, UserPlus, BarChart } from 'lucide-react';
import NavLink from './NavLink';
import AuthButtons from './AuthButtons';
import styles from './Navbar.module.css';

const Navbar = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    api.auth.logout();
    setUser(null);
    navigate('/');
  };

  return (
    <nav className={styles.navbar}>
      <div className={styles.container}>
        <div className={styles.wrapper}>
          <div className={styles.logoContainer}>
            <Link to="/" className={styles.logo}>
              <ShoppingBag className={styles.logoIcon} />
              <span className={styles.logoText}>Mayamarket</span>
            </Link>
          </div>
          <div className={styles.navLinks}>
            <NavLink to="/" icon={Home} text="Home" />
            <NavLink to="/shop" icon={ShoppingBag} text="Shop" />
            {user ? (
              <>
                {user.isAdmin ? (
                  <NavLink to="/admin" icon={BarChart} text="Dashboard" />
                ) : (
                  <Link to="/user/shop" className={styles.dashboardLink}>
                    Shop
                  </Link>
                )}
                <div className={styles.userMenu}>
                  <span className={styles.welcomeText}>
                    Welcome, {user.username}
                  </span>
                  <button
                    onClick={handleLogout}
                    className={styles.logoutButton}
                  >
                    <LogOut className={styles.logoutIcon} />
                    <span>Logout</span>
                  </button>
                </div>
              </>
            ) : (
              <AuthButtons />
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
