import axios from 'axios';

const BASE_URL = 'http://localhost:4990/api';

// Helper function to get auth token
const getAuthToken = () => localStorage.getItem('token');

// Helper function to set auth token
const setAuthToken = (token) => {
  if (token) {
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  } else {
    delete axios.defaults.headers.common['Authorization'];
  }
};

const api = {
  setAuthToken,

  auth: {
    login: async (credentials) => {
      try {
        const response = await axios.post(`${BASE_URL}/users/login`, credentials);
        const { token, user } = response.data;

        // Set token for future requests
        setAuthToken(token);

        return response.data;
      } catch (error) {
        console.error('Login error:', error);
        throw error.response?.data || error;
      }
    },

    register: async (userData) => {
      try {
        const response = await axios.post(`${BASE_URL}/users/register`, userData);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    logout: () => {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setAuthToken(null);
    },

    getProfile: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('No auth token');

        const response = await axios.get(`${BASE_URL}/users/profile`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    updateProfile: async (userData) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('No auth token');

        const response = await axios.put(`${BASE_URL}/users/profile`, userData);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  },

  products: {
    getAll: async (category = 'all', search = '') => {
      try {
        const response = await axios.get(`${BASE_URL}/products`, {
          params: { category, search }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getByUser: async () => {
      try {
        const response = await axios.get(`${BASE_URL}/products/user`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    create: async (productData) => {
      try {
        const response = await axios.post(`${BASE_URL}/products`, productData);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    update: async (productId, productData) => {
      try {
        const response = await axios.put(`${BASE_URL}/products/${productId}`, productData);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    delete: async (productId) => {
      try {
        const response = await axios.delete(`${BASE_URL}/products/${productId}`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  },

  admin: {
    getAnalytics: async () => {
      try {
        const response = await axios.get(`${BASE_URL}/admin/analytics`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getAllUsers: async () => {
      try {
        const response = await axios.get(`${BASE_URL}/admin/users`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    deleteUser: async (userId) => {
      try {
        const response = await axios.delete(`${BASE_URL}/admin/users/${userId}`);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    updateUserRole: async (userId, isAdmin) => {
      try {
        const response = await axios.put(`${BASE_URL}/admin/users/${userId}/role`, { isAdmin });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  }
};

export { api };
