import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ShoppingBag, Home, LogIn, LogOut, UserPlus, BarChart } from 'lucide-react';
import NavLink from './NavLink';
import AuthButtons from './AuthButtons';
import styles from './Navbar.module.css';

const Navbar = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const token = localStorage.getItem('token');
        if (token) {
          const userData = JSON.parse(localStorage.getItem('user'));
          if (userData) {
            setUser(userData);
            setIsAdmin(userData.isAdmin || false);
          }
        }
      } catch (error) {
        console.error('Authentication error:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        setIsAdmin(false);
      }
    };

    checkAuth();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAdmin(false);
    navigate('/');
  };

  return (
    <nav className={styles.navbar}>
      <div className={styles.container}>
        <div className={styles.wrapper}>
          <div className={styles.logoContainer}>
            <Link to="/" className={styles.logo}>
              <ShoppingBag className={styles.logoIcon} />
              <span className={styles.logoText}>M</span>
            </Link>
          </div>
          <div className={styles.navLinks}>
            <NavLink to="/" icon={Home} text="Home" />
            <NavLink to="/shop" icon={ShoppingBag} text="Shop" />
            {user ? (
              <>
                {isAdmin ? (
                  <NavLink to="/admin" icon={BarChart} text="Admin" />
                ) : (
                  <Link to="/dashboard" className={styles.dashboardLink}>
                    Dashboard
                  </Link>
                )}
                <button
                  onClick={handleLogout}
                  className={styles.logoutButton}
                >
                  <LogOut className={styles.logoutIcon} />
                  <span>Logout</span>
                </button>
              </>
            ) : (
              <AuthButtons />
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
