import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
// import { supabase } from '../../lib/supabase';
import { ShoppingBag, Home, LogIn, LogOut, UserPlus, BarChart } from 'lucide-react';
import NavLink from './NavLink';
import AuthButtons from './AuthButtons';

const Navbar = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    supabase.auth.getUser().then(({ data: { user } }) => {
      setUser(user);
      if (user) checkIfAdmin(user.id);
    });

    supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
      if (session?.user) checkIfAdmin(session.user.id);
    });
  }, []);

  const checkIfAdmin = async (userId) => {
    const { data, error } = await supabase
      .from('admin_users')
      .select('id')
      .eq('id', userId)
      .single();

    if (!error && data) {
      setIsAdmin(true);
    }
  };

  const handleLogout = async () => {
    await supabase.auth.signOut();
    navigate('/');
  };

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-2">
              <ShoppingBag className="h-8 w-8 text-orange-500" />
              <span className="text-2xl font-bold text-gray-800">Martfury</span>
            </Link>
          </div>
          <div className="flex items-center space-x-8">
            <NavLink to="/" icon={Home} text="Home" />
            <NavLink to="/shop" icon={ShoppingBag} text="Shop" />
            {user ? (
              <>
                {isAdmin ? (
                  <NavLink to="/admin" icon={BarChart} text="Admin" />
                ) : (
                  <Link to="/dashboard" className="text-gray-700 hover:text-orange-500">
                    Dashboard
                  </Link>
                )}
                <button
                  onClick={handleLogout}
                  className="flex items-center space-x-1 text-gray-700 hover:text-orange-500"
                >
                  <LogOut size={20} />
                  <span>Logout</span>
                </button>
              </>
            ) : (
              <AuthButtons />
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
