import axios from 'axios';

const BASE_URL = 'http://localhost:4990/api';

const getAuthToken = () => localStorage.getItem('token');

const api = {
  auth: {
    register: async (userData) => {
      try {
        const response = await axios.post(`${BASE_URL}/users/register`, userData);
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    login: async (credentials) => {
      try {
        const response = await axios.post(`${BASE_URL}/users/login`, credentials);
        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
        }
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    logout: () => {
      localStorage.removeItem('token');
    }
  },
  orders: {
    create: async (orderItems) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.post(`${BASE_URL}/orders`, orderItems, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  },
  products: {
    getAll: async (category = 'all', search = '') => {
      try {
        const response = await axios.get(`${BASE_URL}/products`, {
          params: {
            category: category,
            search: search
          }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getByUser: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/products/user`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    create: async (productData) => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.post(`${BASE_URL}/products`, productData, {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        return response.data;
      } catch (error) {
        console.error('Error creating product:', error);
        throw error.response?.data || error;
      }
    }
  },
  admin: {
    checkStatus: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/admin/status`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    },

    getAnalytics: async () => {
      try {
        const token = getAuthToken();
        if (!token) throw new Error('Authentication token not found');

        const response = await axios.get(`${BASE_URL}/admin/analytics`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        return response.data;
      } catch (error) {
        throw error.response?.data || error;
      }
    }
  }
};


export { api };
